  {id: 8858, title: "Copyright First Responders Cohort #3, Spring 2016 [Playlist #27819]", slug: nil, subtitle: nil, headnote: "Copyright First Responders will support the work of the University by providing services that help advance teaching, learning, and scholarship through community engagement with copyright. Embedded in each library, archive, or department, Copyright First Responders will develop information expertise, create a collaborative network of support among their peers involved with copyright issues, both locally and across the library, and serve as a resource for the Harvard community by answering copyright questions and sharing critical knowledge.", public: true, casebook_id: nil, ordinals: [], copy_of_id: nil, is_alias: nil, resource_type: nil, resource_id: nil, created_at: "2016-05-16 11:31:01", updated_at: "2017-07-17 09:00:44"},
