  {id: 5313, title: "Public Law & Legislation (University of Ottawa CML 1104) [Playlist #21225]", slug: nil, subtitle: nil, headnote: "Introduction to legislation and public law, focusing on the structure of the Canadian legal system, including: sources of law, the federal legislative process and statutory interpretation; the legal system's constitutional basis; the organization of courts and tribunals in Canada and appeal processes; and the role of the courts in overseeing legislative and administrative action.", public: true, casebook_id: nil, ordinals: [], copy_of_id: nil, is_alias: nil, resource_type: nil, resource_id: nil, created_at: "2015-05-12 13:15:54", updated_at: "2017-07-17 08:59:30"},
